CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240612025135_InitialCreate') THEN
    CREATE TABLE "Authorization" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Token" uuid NOT NULL,
        "Locked" boolean NOT NULL,
        "CreatedAt" timestamp without time zone NOT NULL,
        CONSTRAINT "PK_Authorization" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240612025135_InitialCreate') THEN
    CREATE TABLE "SensorData" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "TimeStamp" timestamp without time zone NOT NULL,
        "DeviceId" macaddr NOT NULL,
        "CarbonDioxide" numeric,
        "Humidity" numeric,
        "Light" boolean,
        "Lpg" numeric,
        "Motion" boolean,
        "Smoke" numeric,
        "Temperature" numeric,
        "AdditionalData" jsonb,
        "ProviderTokenId" integer,
        CONSTRAINT "PK_SensorData" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_SensorData_Authorization_ProviderTokenId" FOREIGN KEY ("ProviderTokenId") REFERENCES "Authorization" ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240612025135_InitialCreate') THEN
    CREATE UNIQUE INDEX "IX_Authorization_Token" ON "Authorization" ("Token");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240612025135_InitialCreate') THEN
    CREATE INDEX "IX_SensorData_ProviderTokenId" ON "SensorData" ("ProviderTokenId");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240612025135_InitialCreate') THEN
    INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
    VALUES ('20240612025135_InitialCreate', '8.0.6');
    END IF;
END $EF$;
COMMIT;
